function toggleFigShortcodeCols(){var e=[];const o=document.querySelectorAll(".fig-shortcode-col-option");for(var t=0;t<o.length;t++)if(o[t].checked){var c=o[t].getAttribute("data-id");e.push(c)}document.getElementById("fig-shortcode").value='[fancy_info columns="'+e+'"]'}document.getElementById("fig-shortcode")&&(document.getElementById("fig-shortcode").onclick=function(){this.select(),document.execCommand("copy")});